def License(): 
    cl=['1234']
    l=open('lic.txt','r')
    l.seek(0)
    hashed_key=l.read()
    l.close()

    hashed_user_key=hashlib.md5(cl[0].encode()).hexdigest()

    if str(hashed_user_key)==str(hashed_key):
        lic.set('PRO')
        title_FRET['text']='FRET  '+str(lic.get())
        License2['text']=lic.get()
        cle.set(cl[0])
        cle_log['fg']='#00ffbb'
        cle_log['bg']='#000'
        cle_box['state']='disabled'
        cle_button['state']='disabled'
        log_activation.set('Licence activée')
        combo_capo['state']='normal'
        combo_ca1['state']='normal'
        combo_ca2['state']='normal'
        combo_ca3['state']='normal'
        combo_ca4['state']='normal'

    else:
        lic.set('BASIC')
        title_FRET['text']='FRET  '+str(lic.get())
        License2['text']=lic.get()
        cle_log['fg']='#ff002f'
        cle_log['bg']='#000'
        cle_box['state']='normal'
        cle_button['state']='normal'
        log_activation.set('Licence non activée')
        combo_capo['state']='disabled'
        combo_ca1['state']='disabled'
        combo_ca2['state']='disabled'
        combo_ca3['state']='disabled'
        combo_ca4['state']='disabled'

def activate():
    if cle.get()!='':
        lo=open('lic.txt','w')
        ll=cle.get()
        li=hashlib.md5(ll.encode()).hexdigest()
        lo.write(str(li))
        lo.close()
    
    else:
        pass
        
