def play_input():
    pygame.midi.init()
    device1=port_id.get();device2=port_id2.get()
    player=pygame.midi.Input(device1);pygame.midi.Output(device2)
    while trans1.get()==1:
        if player.poll():
            event = player.read(1)[0]
            data = event[0]
            timestamp = event[1]
            note_number = data[1]
            velocity = data[2]
            
            #_____________________CHORD_1
            if (str(pygame.midi.midi_to_ansi_note(int(note_number)))=='E4' and velocity!=0):
                ii.set('MI 4')
